const fs = require('fs')
const htmlparser = require("htmlparser2");

function resolveHeadings (content) {
  const headings = []
  let heading;
  let isHeading = false;
  let isTag = true;
  const parser = new htmlparser.Parser({
    onopentag: function (name, attributes) {
      if (name === 'xmp') {
        isTag = false;
      }

      if (isTag) {
        if (
          name === "h1" ||
          name === "h2" ||
          name === "h3" ||
          name === "h4" ||
          name === "h5" ||
          name === "h6"
        ) {
          isHeading = true;
          heading = {
            level: name,
            text: '',
            attributes
          }
          headings.push(heading)
        } else {
          isHeading = false;
        }
      }
    },
    onclosetag: function (name, attributes) {
      if (name === 'xmp') {
        isTag = true;
      }

      if (
        name === "h1" ||
        name === "h2" ||
        name === "h3" ||
        name === "h4" ||
        name === "h5" ||
        name === "h6"
      ) {
        isHeading = false;
      }
    },

    ontext: function(text) {
      if (isHeading) {
        heading.text = text
      }
    }
  });

  parser.write(content);
  parser.end();

  return headings;
}

